        "instructions": [
          {
            "id": 4593,
            "display_text": "Preheat oven to 350°F (180°C).",
            "position": 1,
            "start_time": 0,
            "end_time": 0,
            "temperature": 350,
            "appliance": "oven"
          },
          {
            "appliance": null,
            "id": 4594,
            "display_text": "Slice each chicken breasts in half lengthwise and sprinkle 1 teaspoon of salt, pepper, and garlic powder evenly on both sides of each chicken breast.",
            "position": 2,
            "start_time": 517,
            "end_time": 8367,
            "temperature": null
          },
          {
            "position": 3,
            "start_time": 9367,
            "end_time": 14617,
            "temperature": null,
            "appliance": null,
            "id": 4595,
            "display_text": "Heat oil in a large pan over high heat, then sear the chicken for about 1 minute per side. They will continue to cook through in the oven later on."
          },
          {
            "end_time": 19667,
            "temperature": null,
            "appliance": null,
            "id": 4596,
            "display_text": "In the residual oil (drain some if necessary), toss in the garlic and onions, stirring constantly until the mixture has started to brown.",
            "position": 4,
            "start_time": 15617
          },
          {
            "position": 5,
            "start_time": 20667,
            "end_time": 26817,
            "temperature": null,
            "appliance": null,
            "id": 4597,
            "display_text": "Pour in the cream, stirring until it starts to boil, then add the parmesan and parsley."
          },
          {
            "position": 6,
            "start_time": 27817,
            "end_time": 32650,
            "temperature": null,
            "appliance": null,
            "id": 4598,
            "display_text": "Cook for 1 additional minute, then remove from heat."
          },
          {
            "display_text": "In a 9\" x 13\" (23cm x 33cm)  rectangular baking pan, spread about ¼ of the cream mixture on the bottom.",
            "position": 7,
            "start_time": 33650,
            "end_time": 35917,
            "temperature": null,
            "appliance": null,
            "id": 4599
          },
          {
            "temperature": null,
            "appliance": null,
            "id": 4600,
            "display_text": "Lay enough lasagna noodles to cover the sauce, then spread about ½ of the remaining cream mixture on top.",
            "position": 8,
            "start_time": 37067,
            "end_time": 38867
          },
          {
            "position": 9,
            "start_time": 39867,
            "end_time": 47067,
            "temperature": null,
            "appliance": null,
            "id": 4601,
            "display_text": "Place enough chicken breasts to cover the sauce, then sprinkle with half of the mozzarella, followed by half of the bacon and half of the parsley."
          },
          {
            "start_time": 47617,
            "end_time": 52267,
            "temperature": null,
            "appliance": null,
            "id": 4602,
            "display_text": "Repeat the layers once more, starting with noodles, sauce, chicken, bacon, parsley, and mozzarella.",
            "position": 10
          },
          {
            "id": 4603,
            "display_text": "Bake for about 25 minutes, until the cheese is golden brown.",
            "position": 11,
            "start_time": 0,
            "end_time": 0,
            "temperature": null,
            "appliance": null
          },
          {
            "id": 4604,
            "display_text": "Cool for about 15 minutes, slice, and serve!",
            "position": 12,
            "start_time": 54117,
            "end_time": 64467,
            "temperature": null,
            "appliance": null
          },
          {
            "id": 4605,
            "display_text": "Enjoy!",
            "position": 13,
            "start_time": 65016,
            "end_time": 67567,
            "temperature": null,
            "appliance": null
          }
        ],
