        "instructions": [
          {
            "id": 24829,
            "display_text": "Preheat oven to 375˚F (190˚C).",
            "position": 1,
            "start_time": 0,
            "end_time": 0,
            "temperature": 375,
            "appliance": "oven"
          },
          {
            "end_time": 11930,
            "temperature": null,
            "appliance": null,
            "id": 24830,
            "display_text": "Heat olive oil in a large pot over medium heat. Add onions and garlic. Cook until softened.",
            "position": 2,
            "start_time": 3790
          },
          {
            "temperature": null,
            "appliance": null,
            "id": 24831,
            "display_text": "Add the ground beef, breaking it apart with a spoon, and stir until browned.",
            "position": 3,
            "start_time": 11780,
            "end_time": 14250
          },
          {
            "id": 24832,
            "display_text": "Add the sausage, breaking it apart with a spoon, and stir until browned.",
            "position": 4,
            "start_time": 14100,
            "end_time": 16810,
            "temperature": null,
            "appliance": null
          },
          {
            "temperature": null,
            "appliance": null,
            "id": 24833,
            "display_text": "Mix in 2 tablespoons of the Parmesan, salt, pepper, 3 tablespoons basil, and 2 tablespoons parsley, and stir to combine.",
            "position": 5,
            "start_time": 17240,
            "end_time": 23250
          },
          {
            "position": 6,
            "start_time": 23680,
            "end_time": 33000,
            "temperature": null,
            "appliance": null,
            "id": 24834,
            "display_text": "Add 2 cups (520 g) of the tomato sauce and the tomato paste. Continue stirring until the meat sauce is bubbling. Reduce heat to low, and cover the pot, simmering for 45 minutes."
          },
          {
            "start_time": 34430,
            "end_time": 40000,
            "temperature": null,
            "appliance": null,
            "id": 24835,
            "display_text": "Mix together the ricotta, 2 tablespoons parsley, and 2 tablespoons basil.",
            "position": 7
          },
          {
            "end_time": 46860,
            "temperature": null,
            "appliance": null,
            "id": 24836,
            "display_text": "Place one of the lasagna noodles on a cutting board and cut in half crosswise. Set one half of the noodle aside for later.",
            "position": 8,
            "start_time": 43150
          },
          {
            "display_text": "Evenly spread about 2 tablespoons of the ricotta mixture over the noodle half. Sprinkle 1 tablespoon mozzarella over the noodle.",
            "position": 9,
            "start_time": 48000,
            "end_time": 53060,
            "temperature": null,
            "appliance": null,
            "id": 24837
          },
          {
            "appliance": null,
            "id": 24838,
            "display_text": "Starting from the cut end of the noodle roll upward being careful not to squeeze the mixture out of the noodle. Repeat with the remaining noodles.",
            "position": 10,
            "start_time": 53470,
            "end_time": 58366,
            "temperature": null
          },
          {
            "id": 24839,
            "display_text": "Place the spiral lasagna noodle in a greased muffin tin.",
            "position": 11,
            "start_time": 58900,
            "end_time": 60583,
            "temperature": null,
            "appliance": null
          },
          {
            "id": 24840,
            "display_text": "Sprinkle about 3 tablespoons mozzarella around the noodle spiral.",
            "position": 12,
            "start_time": 60559,
            "end_time": 62920,
            "temperature": null,
            "appliance": null
          },
          {
            "temperature": null,
            "appliance": null,
            "id": 24841,
            "display_text": "Pour 3 tablespoons of the meat sauce over the mozzarella around the noodle spiral.",
            "position": 13,
            "start_time": 63220,
            "end_time": 66619
          },
          {
            "temperature": null,
            "appliance": null,
            "id": 24842,
            "display_text": "Place the other half of the lasagna noodle on a cutting board, and cut it lengthwise.",
            "position": 14,
            "start_time": 69000,
            "end_time": 71380
          },
          {
            "temperature": null,
            "appliance": null,
            "id": 24843,
            "display_text": "Starting from the end of the noodle spiral, tuck the noodle strip into the spiral so the end of noodle spiral is on the outside. Wrap the noodle strip around the spiral while pushing it into the meat sauce. Repeat with the other noodle strip.",
            "position": 15,
            "start_time": 71520,
            "end_time": 75500
          },
          {
            "id": 24844,
            "display_text": "Pour about 2 tablespoons of the sauce in between the noodle “petals”. Sprinkle with about 1 tablespoon of both mozzarella and Parmesan.",
            "position": 16,
            "start_time": 75640,
            "end_time": 82540,
            "temperature": null,
            "appliance": null
          },
          {
            "end_time": 0,
            "temperature": null,
            "appliance": null,
            "id": 24845,
            "display_text": "Bake for about 30 minutes, until the top of the noodles are golden and crispy.",
            "position": 17,
            "start_time": 0
          },
          {
            "appliance": null,
            "id": 24846,
            "display_text": "Let cool for about 5 minutes before removing from muffin tin.",
            "position": 18,
            "start_time": 0,
            "end_time": 0,
            "temperature": null
          },
          {
            "position": 19,
            "start_time": 85990,
            "end_time": 95830,
            "temperature": null,
            "appliance": null,
            "id": 24847,
            "display_text": "Serve over tomato sauce."
          },
          {
            "end_time": 101870,
            "temperature": null,
            "appliance": null,
            "id": 24848,
            "display_text": "Enjoy!",
            "position": 20,
            "start_time": 96130
          }
        ],
