    "instructions": [
      {
        "temperature": null,
        "appliance": null,
        "id": 51606,
        "display_text": "Set a wire rack over a baking sheet.",
        "position": 1,
        "start_time": 0,
        "end_time": 0
      },
      {
        "id": 51607,
        "display_text": "Dust a clean surface with semolina flour. Dust the pizza dough with more flour and divide into 4 portions. Roll out each portion of dough, rotating frequently, to a thin round, dusting with more semolina to prevent sticking. Use a fork to poke holes all over the pizza crusts.",
        "position": 2,
        "start_time": 50333,
        "end_time": 100833,
        "temperature": null,
        "appliance": null
      },
      {
        "display_text": "Gently heat about ¼ inch (½ cm) of olive oil in a large skillet over medium-high heat.  When the oil begins to shimmer, add 1 pizza dough to the pan. Cook for 2 minutes, until golden brown, then flip and cook on the other side for 2 minutes more. Use a fork to puncture any air pockets that rise as the dough cooks. Transfer the cooked pizza crust to the wire rack to cool and sprinkle with flaky salt. Repeat with the remaining dough, adding more olive oil as needed.",
        "position": 3,
        "start_time": 104000,
        "end_time": 148500,
        "temperature": null,
        "appliance": null,
        "id": 51608
      },
      {
        "id": 51609,
        "display_text": "Top the crust with the tomato sauce, Parmigiano-Reggiano cheese, parsley, and garlic chips, if using.",
        "position": 4,
        "start_time": 152000,
        "end_time": 183000,
        "temperature": null,
        "appliance": null
      },
      {
        "id": 51610,
        "display_text": "Slice and serve.",
        "position": 5,
        "start_time": 0,
        "end_time": 0,
        "temperature": null,
        "appliance": null
      },
      {
        "id": 51611,
        "display_text": "Enjoy!",
        "position": 6,
        "start_time": 200000,
        "end_time": 202666,
        "temperature": null,
        "appliance": null
      }
    ],
